package oldsht;

import java.util.Scanner;

 class  football {
     football(){
        Scanner scanner = new Scanner(System.in);
        // String line = scanner.next();
        String line = "";
        byte check = 0;
        for (int i = 0; i < line.length() - 1; i++) {
            if ((line.charAt(i + 1)) == (line.charAt(i)))
                check++;
            else check = 0;
            if (check >= 6)
               break;
        }
        if (check >= 6)
            System.out.println("YES");
        else
            System.out.println("NO");
    }
}
